function hexBuffer2str(BufferArray) {
    let hexArray = [];
    for (let i = 0; i < BufferArray.length; i++) {
        let hex = BufferArray[i].toString(16).toUpperCase().padStart(2, '0');
        hexArray.push(hex);
    }
    return hexArray.join(' ');
}

