function hexBuffer2str(BufferArray) {
    let hexArray = [];
    for (let i = 0; i < BufferArray.length; i++) {
        let hex = BufferArray[i].toString(16).toUpperCase().padStart(2, '0');
        hexArray.push(hex);
    }
    return hexArray.join(' ');
}

msg.payload = hexBuffer2str(msg.payload);
return msg;

var cmd_buffer_array = msg.payload.split(' ').map((s) => parseInt(s, 16));

var command_for_tcp = {
    _session: msg._session,
    host: msg.host,
    port:msg.port
}
command_for_tcp.payload = Buffer.from(cmd_buffer_array);

return command_for_tcp;

node.warn(msg.payload);
if(msg.payload)
msg.payload = msg.payload.toUpperCase();

return msg;

